import React from 'react';
import { Feather, FontAwesome } from '@expo/vector-icons';
import { View, Image, ScrollView, Text, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';


import { firestoreConnect } from 'react-redux-firebase';
import { connect } from 'react-redux';
import { compose } from 'redux';

import QRCode from 'react-native-qrcode-svg';

import logoImg from '../../assets/logo2.png';
import logoCliente from '../../assets/sindpd.png';
import styles from './styles';

class Detail extends React.Component {

    render(){

        const {auth, profile, ids, users} = this.props
        const token = profile.token

        console.log(users)

        const filterToken = ids.filter(id => id.id === token);

        return (
            <View style={styles.container}>
                <View style={styles.header}>
                    <Image source={logoImg} />
                    <TouchableOpacity style={styles.headerText} onPress={() => this.props.navigation.navigate("Inicial")}>
                        <Feather name="arrow-left" size={20} color="#91bd36"
                        />
                    </TouchableOpacity>
                </View>

                <ScrollView showsVerticalScrollIndicator={false}>
                    <View style={styles.card}>
                        <View style={styles.DIV}>
                            <Text style={styles.nomedocliente}>SindPD Digital</Text>
                        </View>
                        <View style={styles.quadrogeral}>

                            <View style={styles.ID}>


                            </View>
                            <View style={styles.coluna}>
                                <View style={styles.dadospessoais}>
                                    <Text style={styles.name}>{filterToken[0].firstName} {filterToken[0].lastName} </Text>
                                    <Text style={styles.descriptionB}>Empresa</Text>
                                    <Text style={styles.description}>{filterToken[0].empresa}</Text>
                                    <Text style={styles.descriptionB}>Cargo</Text>
                                    <Text style={styles.description}>{filterToken[0].cargo}</Text>
                                    <Text style={styles.descriptionB}>Cidade</Text>
                                    <Text style={styles.description}>{filterToken[0].city}, {filterToken[0].uf}</Text>
                                    <Text style={styles.descriptionB}>Contato</Text>
                                    <Text style={styles.description}>{filterToken[0].email}</Text>
                                    <Text style={styles.description}>{filterToken[0].phone}</Text>

                                </View>
                                <View style={styles.Foto}>
                                <Image source={logoCliente} />
                                    <Image
                                        style={{ width: 130, height: 130, borderRadius: 8 }}
                                        source={filterToken[0].url}
                                        resizeMode="cover"
                                    />
                                    <Text style={styles.Titulo}>Profissional de T.I.</Text>
                                </View>
                            </View>

                        </View>

                        <View style={styles.DIV}>

                            <Text style={styles.descriptionWhite}> EXPIRA EM: </Text>
                            <Text style={styles.descriptionWhiteB}> 21/08/2020 </Text>

                        </View>

                        <View style={styles.quadrogeral}>

                            <View style={styles.MainContainer}>
                                <QRCode
                                    value="MECARD: N: Getulio Vargas;  TEL: (11) 2222-2222; EMAIL: getulio@sindpd.com"
                                    size={150}
                                />
                                <View style={{ paddingTop: 10 }} >
                                    <Text style={styles.description}> {filterToken[0].matricula}</Text>
                                    <Text style={styles.descriptionB}> Matrícula </Text>
                                </View>
                            </View>

                        </View>

                        <View style={styles.DIV}>

                            <Text style={styles.Site}> sindpd.org.br </Text>

                        </View>

                    </View>
                </ScrollView>

            </View>
        );
    }

    }



    const mapStateToProps = (state) => {

        console.log(state)
        return{
            auth: state.firebase.auth,
            profile: state.firebase.profile,
            ids: state.firestore.ordered.ids,
            users: state.firestore.ordered.users,
        }
      }

      const mapDispatchToProps = (dispatch) => {
        return {
            editId: (ids, id) => dispatch(editId(ids, id))
        }
      }

      export default compose(
        connect(mapStateToProps, mapDispatchToProps),
        firestoreConnect([
            { collection: 'ids' },
            { collection: 'users' }
        ])
    )(Detail)
