import React, { useState, useEffect } from 'react';
import { View, Text, ScrollView, Image, TextInput, TouchableOpacity } from "react-native";
import * as firebase from "firebase";

import * as ImagePicker from 'expo-image-picker';

import {storage} from '../../../config/fbConfig';

import { Feather } from '@expo/vector-icons';

import { firestoreConnect } from 'react-redux-firebase';
import { connect } from 'react-redux';
import { compose } from 'redux';

import logoImg from '../../../assets/idook.png';
import logoCliente from '../../../assets/sindpd.png';
import styles from './styles';

import { editId } from '../../../store/actions/idActions'


class EditID extends React.Component {

    constructor(props) {
        super(props);
            this.state = {
                url: '',
                image: '',
                setImage:'',

            }
            this.pickImage = this
            .pickImage
            .bind(this);
            this.handleUpload = this
            .handleUpload
            .bind(this);
        }



    async useEffect(){

        if (Constants.platform.ios) {
            const { status } = await ImagePicker.requestCameraRollPermissionsAsync();
            if (status !== 'granted') {
              alert('Sorry, we need camera roll permissions to make this work!');
            }
          }

    }



    signOutUser() {
        firebase.auth().signOut();
    };

    handleChange = (e) => {
        this.setState({
            [e.target.id]:e.target.value
        })
    };
    handleSubmit = (e) => {
        e.preventDefault();
        const {auth, profile, ids} = this.props
        const token = profile.token

        const filterToken = ids.filter(id => id.id === token);

        this.props.editId(this.state, profile.token)

        this.props.navigation.navigate("Settings");
        //this.props.history.push('/');
        //console.log(this.state);
    };

    async pickImage(){

        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.All,
            allowsEditing: true,
            aspect: [4, 3],
            quality: 1,
          });

          //console.log(result)

          if (!result.cancelled) {
            const image = result.uri 
            this.setState(() => ({image}))

            this.uploadImage(image, "test-image")

            //console.log(this.state)
            //this.state.setImage = result.uri
           // this.state.setImage(result.uri);
          }

    }

    uploadImage = async (uri, imageName) => {
        const response = await fetch(uri);
        const blob = await response.blob();

        var ref = firebase.storage().ref().child("images/" + imageName);
        console.log(ref)
        return ref.put(blob);
      }



    render() {

        const {auth, profile, ids} = this.props
        const token = profile.token
       // const setImage = this.state

        const filterToken = ids.filter(id => id.id === token);

        return (
            <View style={styles.container}>
                <View style={styles.header}>
                    <Image style={styles.logo} source={logoCliente} />

                    <TouchableOpacity style={styles.headerText} onPress={() => this.props.navigation.navigate("Settings")}>
                        <Feather name="arrow-left" size={20} color="#91bd36"
                        />
                    </TouchableOpacity>
                </View>


                <View style={{ alignItems: 'flex-start' }}>
                    <Text style={styles.title}>Foto</Text>
                    <Text style={styles.description}>Configure a foto do seu IDook</Text>
                </View>

                <View style={{ justifyContent: 'space-between', marginVertical:50, alignItems: 'center', justifyContent: 'center' }}>
                <Image source={this.state.image || filterToken[0].url} style={{ width: 200, height: 200 }} />
                </View>

                <View style={{marginTop: 20}}>
                <TouchableOpacity onPress={this.pickImage}  style={styles.button}>
                    <Text style={{ color: "#FFF", fontWeight: "500" }}>Selecionar</Text>
                </TouchableOpacity>
                </View>

                <View style={{marginTop: 10}}>
                <TouchableOpacity onPress={this.handleUpload}  style={styles.button}>
                    <Text style={{ color: "#FFF", fontWeight: "500" }}>Upload</Text>
                </TouchableOpacity>
                </View>

            </View>
        );
    }
}

const mapStateToProps = (state) => {

    console.log(state)
    return{
        auth: state.firebase.auth,
        profile: state.firebase.profile,
        ids: state.firestore.ordered.ids,
    }
  }

  const mapDispatchToProps = (dispatch) => {
    return {
        editId: (ids, id) => dispatch(editId(ids, id))
    }
  }

  export default compose(
    connect(mapStateToProps, mapDispatchToProps),
    firestoreConnect([
        { collection: 'ids' }
    ])
)(EditID)
