import React, {setState} from 'react';
import { Feather, FontAwesome } from '@expo/vector-icons';
import { View, ScrollView, Image, Text, TouchableOpacity } from 'react-native';
import * as firebase from "firebase";


import { firestoreConnect } from 'react-redux-firebase';
import { connect } from 'react-redux';
import { compose } from 'redux';

import logoImg from '../../assets/idook.png';
import logoClient from '../../assets/sindpd.png'
import styles from './styles';

import signOut from "../../store/actions/authActions"

class Inicial extends React.Component{


    //console.log(email, displayName)

    signOutUser() {
        firebase.auth().signOut();
    };


    render(){

        const {profile, ids, auth} = this.props

        const uid = this.state.token

        const uid_user = auth.uid

        //console.log(ids);


       if (!auth.uid) {
        this.props.navigation.navigate("Login")
      } 

      console.log(profile)

if(profile.toke){

    return (
        <View style={styles.container}>
            <View style={styles.header}>
            <Image style={styles.logo} source={logoClient} />
            <TouchableOpacity style={styles.headerText} onPress={this.signOutUser} >
            <Feather name="log-out" size={20} color="#91bd36"
                    />
                    <Text>Sair</Text>
                </TouchableOpacity>
            </View>




            <Text style={styles.title}>Bem-vindo,</Text>
            <Text style={styles.description}>Fique por dentro do Sindpd!</Text>



            <ScrollView 
            showsVerticalScrollIndicator = {false}>

            <View style={styles.containerMenu}>
                <View style={styles.menuBox}>
                    <TouchableOpacity
                        style={styles.detailButton}
                        onPress={() => this.props.navigation.navigate("Detail")} 
                    >
                        <FontAwesome name="id-badge" style={styles.icon} />
                        <Text style={styles.detailButtonText}>ID Digital</Text>
                    </TouchableOpacity>
                </View>

                <View style={styles.menuBox}>
                    <TouchableOpacity
                        style={styles.detailButton}
                        onPress={() => this.props.navigation.navigate("Feed")} 
                    >
                        <FontAwesome name="newspaper-o" style={styles.icon} />
                        <Text style={styles.detailButtonText}>Notícias</Text>
                    </TouchableOpacity>
                </View>


                <View style={styles.menuBox}>
                    <TouchableOpacity
                        style={styles.detailButton}
                        onPress={() => this.props.navigation.navigate("Social")} 
                    >
                        <FontAwesome name="users" style={styles.icon}/>
                        <Text style={styles.detailButtonText}>Redes Sociais</Text>
                    </TouchableOpacity>
                </View>

                <View style={styles.menuBox}>
                    <TouchableOpacity
                        style={styles.detailButton}
                        onPress={() => this.props.navigation.navigate("Settings")} 
                    >
                        <FontAwesome name="cogs" style={styles.icon} />
                        <Text style={styles.detailButtonText}>Configuração</Text>
                    </TouchableOpacity>
                </View>



            </View>

            <View style={{alignItems:'flex-end'}}>
                <Image style={styles.logo2} source={logoImg} />
            </View>

            </ScrollView>


        </View>
    );


} else {

    if(ids){

        const filterCPF = ids.filter(id => id.cpf === profile.cpf);

        var hascpf = filterCPF.length;
        //console.log(hascpf)

        if(hascpf != 0){
            return (
                <View style={styles.container}>
                    <View style={styles.header}>
                        <Image style={styles.logo} source={logoCliente} />

                        <TouchableOpacity style={styles.headerText} onPress={this.signOutUser}>
                            <Feather name="arrow-left" size={20} color="#91bd36"
                            />
                        </TouchableOpacity>
                    </View>

                    <View style={{ alignItems: 'flex-start' }}>
                        <Text style={styles.title}>Token,</Text>
                        <Text style={styles.description}>Digite o token enviado pelo Whatsapp</Text>

                    </View>

                    <View style={styles.form}>
                        <View style={{ marginTop: 20 }}>
                            <Text style={styles.inputTitle}>Token</Text>
                            <TextInput
                                style={styles.input}
                                autoCapitalize="none"
                                //onChange={this.handleChange}
                                //id='token'
                                onChangeText={value=> this.setState({token: value})}
                                value={this.state.token}
                            ></TextInput>
                        </View>

                    </View>

                    <TouchableOpacity onPress={this.handleSubmit} style={styles.button} >
                        <Text style={{ color: "#FFF", fontWeight: "500" }}>Confirmar Token</Text>
                    </TouchableOpacity>



                    <View style={{ alignItems: 'flex-end' }}>
                        <Image style={styles.logo2} source={logoImg} />
                    </View>
                </View>
            );
        } else {
            return (
                <View style={styles.container}>
                    <View style={styles.header}>
                        <Image style={styles.logo} source={logoCliente} />

                        <TouchableOpacity style={styles.headerText} onPress={this.signOutUser}>
                            <Feather name="arrow-left" size={20} color="#91bd36"
                            />
                        </TouchableOpacity>
                    </View>

                    <View style={{ alignItems: 'flex-start' }}>
                        <Text style={styles.title}>Que pena :(</Text>
                        <Text style={styles.description}>Infelizmente o seu CPF não foi habilitado na nossa base de dados</Text>

                    </View>  

                    <View style={styles.form}>
                        <View style={{ marginTop: 20 }}>
                            <Text style={styles.inputTitle}>CPF</Text>
                            <TextInput
                                style={styles.input}
                                autoCapitalize="none"
                                defaultValue={profile.cpf}
                                //onChange={this.handleChange}
                                //id='token'
                                onChangeText={token=> this.setState({token: token})}
                                //value={this.state.token}
                            ></TextInput>
                        </View>

                    </View>

                    <TouchableOpacity onPress={this.handleEdit} style={styles.button} >
                        <Text style={{ color: "#FFF", fontWeight: "500" }}>Alterar CPF</Text>
                    </TouchableOpacity>                      

                    <View style={{ alignItems: 'flex-end' }}>
                        <Image style={styles.logo2} source={logoImg} />
                    </View>
                </View>
            );
        }



    }

    return (
        <View style={{
            flex: 1,
            justifyContent:'center',
            alignItems: 'center',
            paddingTop: Constants.statusBarHeight + 20,
        }}>
                <Text>Carregando...</Text>
                <ActivityIndicator size='large'></ActivityIndicator>
            </View>

    )

}

return (
    <View style={{
        flex: 1,
        justifyContent:'center',
        alignItems: 'center',
        paddingTop: Constants.statusBarHeight + 20,
    }}>
            <Text>Carregando...</Text>
            <ActivityIndicator size='large'></ActivityIndicator>
        </View>

)

    }



}

const mapStateToProps = (state) => {

    //  console.log(state)
      return {
          auth: state.firebase.auth,
          profile: state.firebase.profile,
          ids: state.firestore.ordered.ids,
          users: state.firestore.ordered.users,
      }
  }


  export default compose(
      connect(mapStateToProps, null),
      firestoreConnect([
          { collection: 'ids' },
          { collection: 'users' }
      ])
  )(Inicial)
