import { Alert, ToastAndroid } from "react-native";


const initState = {
}


const projectReducer = (state = initState, action) => {
  switch (action.type) {
    case 'CREATE_ID':
     // console.log('ID criada com sucesso!', action.ids);
      ToastAndroid.showWithGravityAndOffset(
        "IDook criada com sucesso!",
        ToastAndroid.LONG,
        ToastAndroid.BOTTOM,
        25,
        50
      );
      return state;
    case 'CREATE_ID_ERROR':
      Alert.alert(
        "ERRO",
        "verifique seus dados",
        [
          { text: "OK" }
        ],
        { cancelable: false }
      );
      //console.log('Erro para criar a ID, tente novamente', action.err);
      return state;
    case 'DELETE_ID':
      console.log('delete id');
      return state;
    case 'DELETE_ID_ERROR':
      Alert.alert(
        "ERRO",
        "verifique seus dados",
        [
          { text: "OK" }
        ],
        { cancelable: false }
      );
      //console.log('delete id error', 'state: ', state, 'action: ', action.id);
      return state;
    case 'EDIT_ID':
      ToastAndroid.showWithGravityAndOffset(
        "IDook editado com sucesso!",
        ToastAndroid.LONG,
        ToastAndroid.BOTTOM,
        25,
        50
      );
      //console.log('edit id');
      return state;
    case 'EDIT_ID_ERROR':
      Alert.alert(
        "ERRO",
        "verifique seus dados",
        [
          { text: "OK" }
        ],
        { cancelable: false }
      );
      //console.log('edit id error', 'state: ', state, 'action: ', action.id);
      return state;
  }
  return state;
};

export default projectReducer;
