import React, { Component } from "react";
import { View, Text, Image, TextInput, TouchableOpacity } from "react-native";

import { connect } from 'react-redux';

import logoImg from '../../assets/idook.png';
import logoCliente from '../../assets/sindpd.png';
import styles from './styles';

import { changeLoginEmail } from '../../store/actions';

class LoginScreen extends Component {


    render() {
        return (
            <View style={styles.container}>
                <View style={styles.header}>
                <Image style={styles.logo} source={logoCliente} />
            </View>  

            <View style={{alignItems:'flex-start'}}>
            <Text style={styles.title}>Olá,</Text>
            <Text style={styles.description}>Bem vindo de volta o seu IDook</Text>

            </View>

                <View style={styles.form}>
                    <View  style={{ marginTop: 32 }}>
                        <Text style={styles.inputTitle}>Email</Text>
                        <TextInput
                            style={styles.input}
                            autoCapitalize="none"
                            value={this.props.email}
          onChangeText={(text) => this.props.changeLoginEmail(text)}
                        ></TextInput>
                    </View>

                    <View style={{ marginTop: 32 }}>
                        <Text style={styles.inputTitle}>Password</Text>
                        <TextInput
                            style={styles.input}
                            secureTextEntry
                            autoCapitalize="none"

                        ></TextInput>
                    </View>
                </View>

                <View style={styles.errorMessage}>
                    {this.props.errorMessage && <Text style={styles.error}>{this.props.errorMessage}</Text>}
                </View>

                <TouchableOpacity style={styles.button} >
                    <Text style={{ color: "#FFF", fontWeight: "500" }}>Sign in</Text>
                </TouchableOpacity>

                <TouchableOpacity
                    style={{ alignSelf: "center", marginTop: 32 }}
                    onPress={() => this.props.navigation.navigate("Register")}
                >
                    <Text style={{ color: "#414959", fontSize: 13 }}>
                        Ainda não tem um IDook? <Text style={{ fontWeight: "700", color: "#000000" }}>Registrar</Text>
                    </Text>
                </TouchableOpacity>

            <View style={{alignItems:'flex-end'}}>
                <Image style={styles.logo2} source={logoImg} />
            </View>
            </View>
        );
    }
}

const mapStateToProps = (state) =>{
    console.log(state)
    return{
        email: state.auth.email, 
        password: state.auth.password
    };
}

export default connect(mapStateToProps, { changeLoginEmail })(LoginScreen)
