import React, { Component } from "react";
import { View, Text, Image, TextInput, TouchableOpacity } from "react-native";
import { connect } from 'react-redux'
import * as firebase from "firebase";
import styles from './styles';
import { Feather } from '@expo/vector-icons';

import { firebaseConnect, firestoreConnect } from 'react-redux-firebase';

import { signOut } from '../../store/actions/authActions'

import logoImg from '../../assets/idook.png';
import logoCliente from '../../assets/sindpd.png';


class Token extends Component {
    state = {
        token: ''
    };

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state);
        //this.props.signUp(this.state);
    }

    render() {
        return (
            <View style={styles.container}>
                <View style={styles.header}>
                    <Image style={styles.logo} source={logoCliente} />

                    <TouchableOpacity style={styles.headerText} onPress={this.props.signOut}>
                        <Feather name="arrow-left" size={20} color="#91bd36"
                        />
                    </TouchableOpacity>
                </View>

                <View style={{ alignItems: 'flex-start' }}>
                    <Text style={styles.title}>Token,</Text>
                    <Text style={styles.description}>Digite o token enviado pelo Whatsapp</Text>

                </View>

                <View style={styles.form}>
                    <View style={{ marginTop: 20 }}>
                        <Text style={styles.inputTitle}>Token</Text>
                        <TextInput
                            style={styles.input}
                            autoCapitalize="none"
                            onChange={this.handleChange}
                            id='token'
                        ></TextInput>
                    </View>

                </View>

                <TouchableOpacity style={styles.button} onPress={this.handleSubmit} >
                    <Text style={{ color: "#FFF", fontWeight: "500" }}>Confirmar Token</Text>
                </TouchableOpacity>



                <View style={{ alignItems: 'flex-end' }}>
                    <Image style={styles.logo2} source={logoImg} />
                </View>
            </View>
        );
    }
}

const mapStateToProps = (state) => {
    console.log(state);
    console.log(state.firestore.ordered.ids);
    return {
      auth: state.firebase.auth,
      profile: state.firebase.profile,
      ids: state.firestore.ordered.ids,
    }
  }

  const mapDispatchToProps = (dispatch) => {
    return {
        signOut: (creds) => dispatch(signOut(creds))
    }
}

export default compose(
    connect(mapStateToProps, mapDistpacthToProps),
    firestoreConnect([
        { collection: 'ids' },
    ])
)(Token)
